mapper.insert:
insert into t_interface_body (interface_code,param,type,parents_param,parents_type,default_value,creator_name,create_time,updater_name,update_time)
select #{interfaceCode},#{param},#{type},#{parentsParam},#{parentsType},#{defaultValue},#{interfaceCreator},#{interfaceCreateTime},#{interfaceUpdater},#{interfaceUpdateTime}
from DUAL where not exists(select interface_code from t_interface_body where interface_code = #{interfaceCode} and param = #{param})
